import './Statistics.sass';

const Statistics = () => {

    
    
    const switchStat = (num: number) => {
        const btns = document.querySelectorAll('.stats__btn');
        
        switch (num) {
            case 1:
                btns[0]?.classList.add('stats__btn__active');
                btns[1]?.classList.remove('stats__btn__active');
                btns[2]?.classList.remove('stats__btn__active');
                btns[3]?.classList.remove('stats__btn__active');
                btns[4]?.classList.remove('stats__btn__active');
    
                break;
            case 2:
                console.log(btns);
                
                btns[0]?.classList.remove('stats__btn__active');
                btns[1]?.classList.add('stats__btn__active');
                btns[2]?.classList.remove('stats__btn__active');
                btns[3]?.classList.remove('stats__btn__active');
                btns[4]?.classList.remove('stats__btn__active');
                console.log(btns[1]);
    
                break;
            case 3:
                btns[0]?.classList.remove('stats__btn__active');
                btns[1]?.classList.remove('stats__btn__active');
                btns[2]?.classList.add('stats__btn__active');
                btns[3]?.classList.remove('stats__btn__active');
                btns[4]?.classList.remove('stats__btn__active');
        
                break;
            case 4:
                btns[0]?.classList.remove('stats__btn__active');
                btns[1]?.classList.remove('stats__btn__active');
                btns[2]?.classList.remove('stats__btn__active');
                btns[3]?.classList.add('stats__btn__active');
                btns[4]?.classList.remove('stats__btn__active');
        
                break;
            case 5:
                btns[0]?.classList.remove('stats__btn__active');
                btns[1]?.classList.remove('stats__btn__active');
                btns[2]?.classList.remove('stats__btn__active');
                btns[3]?.classList.remove('stats__btn__active');
                btns[4]?.classList.add('stats__btn__active');
        
                break;
        }
    }

    return (
        <div className="stats none">
            <button className="stats__btn stats__btn__active" onClick={() => {switchStat(1)}}>За день</button>
            <button className="stats__btn" onClick={() => {switchStat(2)}}>За тиждень</button>
            <button className="stats__btn" onClick={() => {switchStat(3)}}>За місяць</button>
            <button className="stats__btn" onClick={() => {switchStat(4)}}>За рік</button>
            <button className="stats__btn" onClick={() => {switchStat(5)}}>За весь час</button>
        </div>
    )
}

export default Statistics;
