import './Adding.sass';
import {Component} from 'react';

class Adding extends Component {
  constructor(props) {
    super(props);
    this.state = {
      budgetQueue: []
    };
    this.elementAdd = this.elementAdd.bind(this);
  }

  elementAdd(event) {
    event.preventDefault();
    const source = event.target.parentElement.parentElement.querySelector('.adding__input[type=text]').value;
    const amount = parseInt(event.target.parentElement.parentElement.querySelector('.adding__input[type=number]').value);
    if (event.target.textContent === 'Додати прибуток') {
      const newIncome = { source, amount, type: 'income' };
      this.setState(prevState => ({ budgetQueue: [...prevState.budgetQueue, newIncome] }));
    } else if (event.target.textContent === 'Додати витрату') {
      const newExpense = { source, amount, type: 'expense' };
      this.setState(prevState => ({ budgetQueue: [...prevState.budgetQueue, newExpense] }));
    }
  }

  render() {
    return (
      <div className="adding">
        <form action="" className="adding__form">
          <input type="text" className="adding__input" placeholder="Джерело доходу / ціль витрати" />
          <input type="number" className="adding__input" placeholder="Введіть суму" />
          <div className="adding__buttons">
            <button onClick={this.elementAdd} type="submit" className="adding__btn">
              Додати прибуток
            </button>
            <button onClick={this.elementAdd} type="submit" className="adding__btn">
              Додати витрату
            </button>
          </div>
        </form>
      </div>
    );
  }
}

export default Adding;
