import './List.sass';
import Search from '../search/Search';
import edit from '../../../icons/edit.svg';
import deleteIcon from '../../../icons/delete.svg';
import { Context } from '../../..';
import { useContext, useEffect, useState } from 'react';
import { ListItem } from '../../../models/ListItem';

export interface I {
	i: number
}

export const UpdateList = ({i}: I) => {

	const [items, setItems] = useState<Array<ListItem>>([]);
	const {store} = useContext(Context);

	console.log(i);
	
    useEffect(() => {
		async function fetchData() {
			const res = await store.generate_list();
			setItems(res);
		}
		fetchData();
	// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [i]);

	async function fetchData() {
		const res = await store.generate_list();
		setItems(res);
	}

	const [tooltipText, setTooltipText] = useState('Клацніть, щоб скопіювати');

	const handleCopyAmount = (event: React.MouseEvent<HTMLTableCellElement>) => {
		const cell = event.target as HTMLElement;
		const amount = cell.textContent;
	
		if (amount) {
			navigator.clipboard.writeText(amount);
			setTooltipText('Текст успішно скопійовано');
			setTimeout(() => {
				setTooltipText('Клацніть, щоб скопіювати');
		}, 2000);
		}
	};

	return (
		<tbody>
			{items.map((element) => (
				<tr key={Number(element.item_id)}>
					<td>
						<button onClick={() => fetchData()} className="edit"><img src={edit} alt="Edit icon" /></button>
					</td>
					<td>{element.description}</td>
					<td
						style={{color: element.bool ? '#27AE60' : '#FC4C4F'}}
						data-tooltip={tooltipText}
						onClick={handleCopyAmount}
					>{element.value}</td>
					<td>{element.date}</td>
					<td>
						<button onClick={(event) => {
								event.preventDefault(); 
									store.delete(); 
									document.querySelectorAll('input').forEach(element => {
									element.value = '';
								});
							}} 
						className="delete"><img src={deleteIcon} alt="Delete icon" /></button>
					</td>
				</tr>
			))}
        </tbody>
	)
}

const List = () => {

	let i = 1;

    return(
        <div className='list'>
			<Search/>
            <table>
                <thead>
					<tr>
						<th><button onClick={() => i++}>Ред.</button></th>
						<th>Опис</th>
						<th>Сума</th>
						<th>Дата</th>
						<th>Вид.</th>
					</tr>
                </thead>
                <UpdateList i={i}></UpdateList>
            </table>
        </div>      
    )
}

export default List;
