import './Statistics.sass';

export const switchStat = (num: number, elements: string, active: string) => {

    const btns = document.querySelectorAll(elements);
    
    switch (num) {
        case 1:
            btns.forEach((element, index) => {
                index === 0 ? element.classList.add(active) : element.classList.remove(active);
            })
            break;
        case 2:        
            btns.forEach((element, index) => {
                index === 1 ? element.classList.add(active) : element.classList.remove(active);
            })
            break;
        case 3:
            btns.forEach((element, index) => {
                index === 2 ? element.classList.add(active) : element.classList.remove(active);
            })
            break;
        case 4:
            btns.forEach((element, index) => {
                index === 3 ? element.classList.add(active) : element.classList.remove(active);
            })
            break;
        case 5:
            btns.forEach((element, index) => {
                index === 4 ? element.classList.add(active) : element.classList.remove(active);
            })
            break;
    }
}

const Statistics = () => {
    return (
        <div className="stats none">
            <button className="stats__btn stats__btn__active" onClick={() => {switchStat(1, '.stats__btn', 'stats__btn__active')}}>За день</button>
            <button className="stats__btn" onClick={() => {switchStat(2, '.stats__btn', 'stats__btn__active')}}>За тиждень</button>
            <button className="stats__btn" onClick={() => {switchStat(3, '.stats__btn', 'stats__btn__active')}}>За місяць</button>
            <button className="stats__btn" onClick={() => {switchStat(4, '.stats__btn', 'stats__btn__active')}}>За рік</button>
            <button className="stats__btn" onClick={() => {switchStat(5, '.stats__btn', 'stats__btn__active')}}>За весь час</button>
        </div>
    )
}

export default Statistics;
