    switch (num) {
        case 1:
            history = 1;
            statistics = 0;
            break;
        case 2:
            history = 0;
            statistics = 1;
            break;
        default:
            history = 0;
            statistics = 0;
      }
}

const Menu = () => {
    return(
        <div className='menu'>
            <button onClick={() => {selectArea(1); console.log(history);}} className="menu__btn">Історія</button>
            <button onClick={() => selectArea(2)} className="menu__btn">Статистика</button>
        </div>      
    )
}

export default Menu;
