import './SumArea.sass';

import { useState } from 'react';

function SumArea() {
	const [amount, setAmount] = useState(0);
	const [percent, setPercent] = useState(0);
	const [daily, setDaily] = useState(0);
	const [weekly, setWeekly] = useState(0);
	const [period, setPeriod] = useState(0);

	const handleAmountChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newAmount = parseFloat(event.target.value);
		setAmount(newAmount);
		setDaily(newAmount / 30);
		setWeekly(newAmount / 4);
	}

	const handlePercentChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newPercent = parseFloat(event.target.value);
		setPercent(newPercent);
  	}

	const handlePeriodChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newPeriod = parseFloat(event.target.value);
		setPeriod(newPeriod);
  	}

	const handleCalculate = () => {
		const savings = amount * percent / 100;
		const totalExpenses = period * daily;
		const remaining = amount - totalExpenses;
		const remainingWithSavings = remaining + savings;
		alert(`Загальні витрати ${period} дні/-ів: ${totalExpenses.toFixed(2)}
		Допустимі витрати на 1 день місяця: ${daily}
		Допустимі витрати на 1 тиждень місяця: ${weekly}
		Залишок після витрат: ${remaining.toFixed(2)}
		Заощадження: ${savings.toFixed(2)}
		Залишок з урахуванням заощаджень: ${remainingWithSavings.toFixed(2)}`);
 	}

	return (
		<div>
			<h2>Опрацювання суми</h2>
			<label>
				Введіть суму:
				<input type="number" value={amount} onChange={handleAmountChange} />
			</label>
			<br />
			<label>
				Введіть відсоток для заощадження:
				<input type="number" value={percent} onChange={handlePercentChange} />
			</label>
			<br />
			<label>
				Введіть термін у днях:
				<input type="number" value={period} onChange={handlePeriodChange} />
			</label>
			<br />
			<button onClick={handleCalculate}>Обрахувати</button>
		</div>
	)
}

export default SumArea;
