import { Component } from 'react';
import './Menu.sass';

export let history = 0;
export let statistics = 0;

const selectArea = (num: number) => {
    switch (num) {
        case 1:
            history = 1;
            statistics = 0;
            break;
        case 2:
            history = 0;
            statistics = 1;
            break;
        default:
            history = 0;
            statistics = 0;
      }
}

class Menu extends Component {
    handleClick = (num: number) => {
      selectArea(num);
      this.forceUpdate();
    };
  
    render() {
      return (
        <div className="menu">
          <button className='menu__btn' onClick={() => this.handleClick(1)}>Історія</button>
          <button className='menu__btn' onClick={() => this.handleClick(2)}>Статистика</button>
          <p>Історія: {history}</p>
          <p>Статистика: {statistics}</p>
        </div>
      );
    }
  }

export default Menu;
