export const switchButtons = (num: number, data: any[], elements: string, active: string) => {

    const btns = document.querySelectorAll(elements);
    const btnsContent: any[] = [];
    data.forEach((element) => {
        btnsContent.push(document.querySelector(element));
    })
    
    switch (num) {
        case 1:
            btns.forEach((element, index) => {
                index === 0 ? element.classList.add(active) : element.classList.remove(active);
            })
            console.log(data);
            
            if (btnsContent) {
                btnsContent.forEach((element, index) => {
                    index === 0 ? element?.classList.remove('none') : element?.classList.add('none');
                })
            }
            break;
        case 2:        
            btns.forEach((element, index) => {
                index === 1 ? element.classList.add(active) : element.classList.remove(active);
            })
            if (btnsContent) {
                btnsContent.forEach((element, index) => {
                    index === 1 ? element?.classList.remove('none') : element?.classList.add('none');
                })
            }
            break;
        case 3:
            btns.forEach((element, index) => {
                index === 2 ? element.classList.add(active) : element.classList.remove(active);
            })
            if (btnsContent) {
                btnsContent.forEach((element, index) => {
                    index === 2 ? element?.classList.remove('none') : element?.classList.add('none');
                })
            }
            break;
        case 4:
            btns.forEach((element, index) => {
                index === 3 ? element.classList.add(active) : element.classList.remove(active);
            })
            if (btnsContent) {
                btnsContent.forEach((element, index) => {
                    index === 3 ? element?.classList.remove('none') : element?.classList.add('none');
                })
            }
            break;
        case 5:
            btns.forEach((element, index) => {
                index === 4 ? element.classList.add(active) : element.classList.remove(active);
            })
            if (btnsContent) {
                btnsContent.forEach((element, index) => {
                    index === 4 ? element?.classList.remove('none') : element?.classList.add('none');
                })
            }
            break;
    }
}

const Menu = () => {
    const btnsContent: any[] = [
        '.list',
        '.stats',
        '.calculator'
    ];
    return(
        <div className='menu'>
            <button className="menu__btn menu__btn__active" onClick={() => switchButtons(1, btnsContent, '.menu__btn', 'menu__btn__active')}>Історія</button>
            <button className="menu__btn" onClick={() => switchButtons(2, btnsContent, '.menu__btn', 'menu__btn__active')}>Статистика</button>
            <button className="menu__btn" onClick={() => switchButtons(3, btnsContent, '.menu__btn', 'menu__btn__active')}>Калькулятор</button>
        </div>      
    )
}

export default Menu;
