import './List.sass';
import Search from '../search/Search';
import edit from '../../../icons/edit.svg';
import deleteIcon from '../../../icons/delete.svg';
import { Context } from '../../..';
import { useContext, useEffect, useRef, useState } from 'react';
import { ListItem } from '../../../models/ListItem';

type MouseEvent = React.MouseEvent<HTMLTableSectionElement, globalThis.MouseEvent>;

export const UpdateList = () => {

	// Генерация и обновление списка
	const [items, setItems] = useState<Array<ListItem>>([]);
	const {store} = useContext(Context);

	const [bool, setBool] = useState(false);
	const changeBool = () => {	
		console.log(1);
		
		setBool(bool => !bool);
	}

	useEffect(() => {
		async function fetchData() {
			const res = await store.generate_list();
			setItems(res);
		}
		fetchData();
	// eslint-disable-next-line react-hooks/exhaustive-deps
	}, [bool]);


	// Копирование текста по нажатию
	const [tooltipText, setTooltipText] = useState('Клацніть, щоб скопіювати');

	const handleCopyAmount = (event: React.MouseEvent<HTMLTableCellElement>) => {
		const cell = event.target as HTMLElement;
		const amount = cell.textContent;
	
		if (amount) {
			navigator.clipboard.writeText(amount);
			setTooltipText('Текст успішно скопійовано');
			setTimeout(() => {
				setTooltipText('Клацніть, щоб скопіювати');
		}, 2000);
		}
	};

	// Скролл
	const [startY, setStartY] = useState(0);
    const [startX, setStartX] = useState(0);
    const [scrollLeft, setScrollLeft] = useState(0);
    const [scrollTop, setScrollTop] = useState(0);
    const [isDown, setIsDown] = useState(false);
  
    const areaRef = useRef<HTMLTableSectionElement>(null);
  
    const mouseIsDown = (e: MouseEvent) => {
		setIsDown(true);
		const area = areaRef.current;
		if (!area) return;
		setStartY(e.pageY - area.offsetTop);
		setStartX(e.pageX - area.offsetLeft);
		setScrollLeft(area.scrollLeft);
		setScrollTop(area.scrollTop);
    };
  
    const mouseUp = () => {
      	setIsDown(false);
    };
  
    const mouseLeave = () => {
      	setIsDown(false);
    };
  
    const mouseMove = (e: MouseEvent) => {
      	if (isDown) {
			e.preventDefault();
			const area = areaRef.current;
			if (!area) return;
			//Рух по вертикалі
			const y = e.pageY - area.offsetTop;
			const walkY = y - startY;
			area.scrollTop = scrollTop - walkY;
	
			//Рух по горизонталі
			const x = e.pageX - area.offsetLeft;
			const walkX = x - startX;
			area.scrollLeft = scrollLeft - walkX;
      }
    };

	return (
		<tbody
			ref={areaRef}
			onMouseDown={mouseIsDown}
			onMouseUp={mouseUp}
			onMouseLeave={mouseLeave}
			onMouseMove={mouseMove}
		>
			{items.map((element) => (
				<tr key={Number(element.item_id)}>
					<td>
						<button onClick={() => changeBool()} className="edit"><img src={edit} alt="" /></button>
					</td>
					<td>{element.description}</td>
					<td
						style={{color: element.bool ? '#27AE60' : '#FC4C4F'}}
						data-tooltip={tooltipText}
						onClick={handleCopyAmount}
					>{element.value}</td>
					<td>{element.date}</td>
					<td>
						<button onClick={() => {
							store.delete();
							setTimeout(changeBool, 100);
						}} 
							className="delete"><img src={deleteIcon} alt="Delete icon" />
						</button>
					</td>
				</tr>
			))}
        </tbody>
	)
}

const List = () => {
    return(
        <div className='list'>
			<Search/>
            <table>
                <thead>
					<tr>
						<th>Ред.</th>
						<th>Опис</th>
						<th>Сума</th>
						<th>Дата</th>
						<th>Вид.</th>
					</tr>
                </thead>
                <UpdateList></UpdateList>
            </table>
        </div>      
    )
}

export default List;
