import "./Chart.sass";
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from "chart.js";
import { Bar } from "react-chartjs-2";
import React, { useState, useEffect } from "react";

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

const Chart = () => {
    const [chartData, setChartData] = useState<{
        labels: string[];
        datasets: {
          label: string;
          data: number[];
          borderColor: string;
          backgroundColor: string;
        }[];
      }>({
        labels: [],
        datasets: [],
      });

  const [chartOptions, setChartOptions] = useState({});

  useEffect(() => {
    setChartData({
      labels: ["John", "Kevin", "Geroge", "Micheal", "Oreo"],
      datasets: [
        {
          label: "Витрати",
          data: [34, 3, 98, 560, 154],
          borderColor: "rgb(251, 63, 12)",
          backgroundColor: "rgba(251, 63, 12, 0.4)",
        },
        {
            label: "Доходи",
            data: [12, 55, 34, 120, 720],
            borderColor: "rgb(53, 235, 96)",
            backgroundColor: "rgba(53, 235, 96, 0.4)",
          },
      ],
    });
    setChartOptions({
      responsive: true,
      plugins: {
        legend: {
          position: "top",
        },
        title: {
          display: true,
          text: "Бюджет",
        },
      },
    });
  }, []);

  return (
    <div className="chart">
      <Bar options={chartOptions} data={chartData} />
    </div>
  );
}

export default Chart;
