import './Adding.sass';
import { Context } from '../../..';
import { useContext, useState } from 'react';

const Adding = () => {

    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [description, setDescription] = useState<string>('');
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const [value, setValue] = useState<string>('');
    // eslint-disable-next-line react-hooks/rules-of-hooks
    const {store} = useContext(Context);

    const add_income = document.querySelector('.adding__btn_1');
    const add_expense = document.querySelector('.adding__btn_2');

    add_income?.addEventListener('mouseover', () => {
        add_income.classList.add('adding__btn_z-index');
        add_expense?.classList.remove('adding__btn_z-index');
    });

    add_income?.addEventListener('mouseout', () => {
        add_income.classList.add('adding__btn_z-index');
        add_expense?.classList.remove('adding__btn_z-index');
    });
  
    return (
        <div className="adding">
            <form action="" className="adding__form">
                <input 
                    onChange={e => setDescription(e.target.value)}
                    value={description}
                    type="text" 
                    className="adding__input" 
                    placeholder="Прибуток / Витрата" 
                />
                <input 
                    onChange={e => setValue(e.target.value)}
                    value={value}
                    type="number" 
                    className="adding__input" 
                    placeholder="Введіть суму" 
                />
                <div className="adding__buttons">
                    <button className="adding__btn adding__btn_1" 
                        onClick={(event) => {
                            event.preventDefault(); 
                            store.add(description, value, true); 
                            document.querySelectorAll('input').forEach(element => {
                                element.value = '';
                            });
                        }}>
                        
                        Додати
                    </button>
                    <button className="adding__btn adding__btn_2" 
                        onClick={(event) => {
                            event.preventDefault(); 
                            store.add(description, value, false); 
                            document.querySelectorAll('input').forEach(element => {
                                element.value = '';
                            });
                        }}>
                        Додати
                    </button>
                </div>
            </form>
        </div>
    )
}

export default Adding;
