import './Calculator.sass';

import SumArea from './sumArea/SumArea';
import CalcArea from './calcArea/CalcArea';
import { Component } from 'react';


interface CalculatorProps {}

interface CalculatorState {
  activeBtnIndex: number;
}

class Calculator extends Component<CalculatorProps, CalculatorState> {
  constructor(props: CalculatorProps) {
    super(props);

    this.state = {
      activeBtnIndex: 1,
    };
  }

  switchButtons = (index: number, btnsContent: any[], btnSelector: string, activeClass: string) => {
    const buttons = document.querySelectorAll(btnSelector);
    buttons.forEach((button, i) => {
      if (i === index - 1) {
        button.classList.add(activeClass);
      } else {
        button.classList.remove(activeClass);
      }
    });
    const contentContainers = btnsContent.map((selector) => document.querySelector(selector));
    contentContainers.forEach((container, i) => {
      if (i === index - 1) {
        container?.classList.remove('none');
      } else {
        container?.classList.add('none');
      }
    });
    this.setState({
      activeBtnIndex: index,
    });
  };

  render() {
    const { activeBtnIndex } = this.state;

    return (
      <div className="calculator none">
        <div className="calcArea__buttons">
          <button className={`calcArea__btn ${activeBtnIndex === 1 ? 'calcArea__btn__active' : ''}`} onClick={() => {this.switchButtons(1, ['.sum-area', '.calc-area'], '.calcArea__btn', 'calcArea__btn__active')}}>
            Суми
          </button>
          <button className={`calcArea__btn ${activeBtnIndex === 2 ? 'calcArea__btn__active' : ''}`} onClick={() => {this.switchButtons(2, ['.sum-area', '.calc-area'], '.calcArea__btn', 'calcArea__btn__active')}}>
            Відсотків
          </button>
        </div>
        <div className={`sum-area ${activeBtnIndex === 1 ? '' : 'none'}`}>
          <SumArea />
        </div>
        <div className={`calc-area none ${activeBtnIndex === 2 ? '' : 'none'}`}>
          <CalcArea />
        </div>
      </div>
    );
  }
}

export default Calculator;
