import './SumArea.sass';

import { useState, useRef } from 'react';

function SumArea() {
	const [amount, setAmount] = useState(0);
	const [percent, setPercent] = useState(0);
	const [daily, setDaily] = useState(0);
	const [weekly, setWeekly] = useState(0);
	const [period, setPeriod] = useState(0);

	const handleAmountChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newAmount = parseFloat(event.target.value);
		setAmount(newAmount);
	}

	const handlePercentChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newPercent = parseFloat(event.target.value);
		setPercent(newPercent);
  	}

	const handlePeriodChange = (event:React.ChangeEvent<HTMLInputElement>) => {
		const newPeriod = parseFloat(event.target.value);
		setPeriod(newPeriod);
  	}

	const rightWordEnding = (number:number, text: string[], cases = [2, 0, 1, 1, 1, 2]) =>  text[(number % 100 > 4 && number % 100 < 20) ? 2 : cases[(number % 10 < 5) ? number % 10 : 5]];

	const outputRef = useRef<HTMLDivElement | null>(null);

	const handleCalculate = () => {
		if (!amount || amount <= 0) {
			alert('Введіть коректну суму!');
			return;
		}
		if (!percent || percent <= 0 || percent > amount) {
			alert('Введіть коректний відсоток!');
			return;
		}
		if (!period || period <= 0) {
			alert('Введіть коректний період!');
			return;
		}

		let savings = amount * percent / 100;
		let totalExpenses = period * daily;
		let remaining = amount - totalExpenses;
		let remainingWithSavings = remaining + savings;
		let sum = amount;
	  
		if (outputRef.current) {
			outputRef.current.innerHTML = '';
			if(period === 1 && savings < sum) {
				setDaily(sum);
				setWeekly(sum = (sum-savings) / 4);
				outputRef.current.innerHTML += `<p>Можна витратити за ${period} день: ${(sum = sum-savings).toFixed(2)}</p>`
				remaining = 0;
			} else if (period > 1 && savings < totalExpenses) {
				setDaily(sum = (totalExpenses-savings) / 30);
				setWeekly(sum = (totalExpenses-savings) / 4);
				outputRef.current.innerHTML += `<p>Можна витратити за ${period} ${rightWordEnding(period, ['день', 'дні', 'днів'])}: ${(totalExpenses-savings).toFixed(2)}</p>`
			}
			outputRef.current.innerHTML += `<p>Заощадження: ${savings.toFixed(2)}</p>`;
			outputRef.current.innerHTML += `<p>Допустимі витрати на 1 день місяця: ${daily.toFixed(2)}</p>`;
			outputRef.current.innerHTML += `<p>Допустимі витрати на 1 тиждень місяця: ${weekly.toFixed(2)}</p>`;
			outputRef.current.innerHTML += `<p>Залишок після витрат: ${remaining.toFixed(2)}</p>`;
			outputRef.current.innerHTML += `<p>Залишок з урахуванням заощаджень: ${remainingWithSavings.toFixed(2)}</p>`;
		}
	}
	  
	  return (
		<div>
		  <h2>Опрацювання суми</h2>
		  <label>
			Введіть суму:
			<input type="number" value={amount} onChange={handleAmountChange} />
		  </label>
		  <br />
		  <label>
			Введіть відсоток для заощадження:
			<input type="number" value={percent} onChange={handlePercentChange} />
		  </label>
		  <br />
		  <label>
			Введіть термін у днях:
			<input type="number" value={period} onChange={handlePeriodChange} />
		  </label>
		  <br />
		  <button onClick={handleCalculate}>Обрахувати</button>
		  <div ref={outputRef}></div>
		</div>
	  )
}	  

export default SumArea;
