import { Component } from 'react';
import './Menu.sass';

let history:number;
let statistics:number;

const selectArea = (num: number) => {
    switch (num) {
        case 1:
            history = 1;
            statistics = 0;
            break;
        case 2:
            history = 0;
            statistics = 1;
            break;
        default:
            history = 0;
            statistics = 0;
      }
      exports.history = history;
      exports.statistics = statistics;
}

class Menu extends Component {
    handleClick = (num: number) => {
      selectArea(num);
      this.forceUpdate();
    };
  
    render() {
      return (
        <div className="menu">
          <button onClick={() => this.handleClick(1)}>Історія</button>
          <button onClick={() => this.handleClick(2)}>Статистика</button>
        </div>
      );
    }
  }

  export { history, statistics, Menu };
