import './Menu.sass';

const changeArea = (num: number) => {

    const btns = document.querySelectorAll('.menu__btn');
    const list = document.querySelector('.list');
    const stats = document.querySelector('.stats-wrapper');
    const calculator = document.querySelector('.calculator');

    switch (num) {
        case 1:
            list?.classList.remove('none');
            stats?.classList.add('none');
            calculator?.classList.add('none');

            btns[0]?.classList.add('menu__btn__active');
            btns[1]?.classList.remove('menu__btn__active');
            btns[2]?.classList.remove('menu__btn__active');

            break;
        case 2:
            list?.classList.add('none');
            stats?.classList.remove('none');
            calculator?.classList.add('none');

            btns[0]?.classList.remove('menu__btn__active');
            btns[1]?.classList.add('menu__btn__active');
            btns[2]?.classList.remove('menu__btn__active');

            break;
            case 3:
                list?.classList.add('none');
                stats?.classList.add('none');
                calculator?.classList.remove('none');
    
                btns[0]?.classList.remove('menu__btn__active');
                btns[1]?.classList.remove('menu__btn__active');
                btns[2]?.classList.add('menu__btn__active');
    
                break;
    }
}

const Menu = () => {
    return(
        <div className='menu'>
            <button className="menu__btn menu__btn__active" onClick={() => changeArea(1)}>Історія</button>
            <button className="menu__btn" onClick={() => changeArea(2)}>Статистика</button>
            <button className="menu__btn" onClick={() => changeArea(3)}>Калькулятор</button>
        </div>      
    )
}

export default Menu;
