import './List.sass';
import Search from '../search/Search';

import edit from '../../../icons/edit.svg';
import deleteIcon from '../../../icons/delete.svg';




class listItemСlass {
    constructor(bdDescr = "", bdAmount = "0", bdFlag = true) {
        this.descrition = bdDescr;
        this.amount = bdAmount;
        this.flag = bdFlag;
    }
  
    get descrition():string {
        return this.descrition;
    }
  
    set descrition(newDescr) {
        this.descrition = newDescr;
    }
  
    get amount():string {
        return this.amount;
    }
  
    set amount(newAmount) {
        if (typeof newAmount === 'string' && Number(newAmount) >= 0) {
            this.amount = newAmount;
        } else {
            throw new Error('Invalid amount value');
        }
    }
  
    get flag():boolean {
        return this.flag;
    }
  
    set flag(newFlag) {
        if (typeof newFlag === 'boolean') {
            this.flag = newFlag;
        } else {
            throw new Error('Invalid flag value');
        }
    }
}

const items = [
	{ id: 1, description: 'Item 1', amount: '10.00', flag: true, date: '2023-04-24' },
	{ id: 2, description: 'Item 2', amount: '20.00', flag: false, date: '2023-04-23' },
	{ id: 3, description: 'Item 3', amount: '30.00', flag: true, date: '2023-04-22' },
	{ id: 4, description: 'Item 4', amount: '40.00', flag: false, date: '2023-04-21' },
	{ id: 5, description: 'Item 5', amount: '50.00', flag: true, date: '2023-04-20' }
  ];
  

const List = () => {
    return(
        <div className='list'>
            <table>
                <thead>
                    <Search/>
                </thead>
                <tbody>
				{/* {items.map((item:object) => (
					<tr key={item.id}>
						<td><div className="edit"><img src={edit} alt="Edit icon" /></div></td>
						<td>{item.description}</td>
						<td>{item.amount}</td>
						<td>{item.date}</td>
						<td><div className="delete"><img src={deleteIcon} alt="Delete icon" /></div></td>
					</tr>
				))} */}
                </tbody>
            </table>
        </div>      
    )
}

export default List;
