import {FC, useState, useContext} from 'react';
import { Context } from '../../../../index';
import { observer } from 'mobx-react-lite';
import './EditForm.sass';

const EditForm: FC = () => {

    const [description, setDescription] = useState<string>('');
    const [value, setValue] = useState<string>('');
    const {store} = useContext(Context);

    return (
        <div className="edit-form">
            <div className="edit-form__closeBtn" onClick={() => document.querySelector('.edit-form')?.classList.add('none')}>
                    <span className="edit-form__line edit-form__line-1"></span>
                    <span className="edit-form__line edit-form__line-2"></span>
            </div>
            <div className='edit-form__content'>
                <div className="edit-form__title">Редагування елементу</div>
                <div className="edit-form__form">
                    <input className='edit-form__input'
                        onChange={e => setDescription(e.target.value)}
                        value={description}
                        type="text" 
                        placeholder='Новий опис'
                    />
                    <input className='edit-form__input'
                        onChange={e => setValue(e.target.value)}
                        value={value}
                        type="number" 
                        placeholder='Нова сумма'
                    />
                    <div className="edit-form__wrapper">
                            <button className="adding__btn adding__btn_1" 
                                onMouseEnter={() => {
                                    document.querySelector('.adding__btn_1')?.classList.add('adding__btn_z-index');
                                    document.querySelector('.adding__btn_2')?.classList.remove('adding__btn_z-index');                            
                                }}
                                onClick={(event) => {
                                    event.preventDefault(); 
                                    store.add(description, value, true); 
                                    document.querySelectorAll('input').forEach(element => {
                                        element.value = '';
                                    });
                                }}>
                                Прибуток
                            </button>
                            <button className="adding__btn adding__btn_2" 
                                onMouseEnter={() => {
                                    document.querySelector('.adding__btn_1')?.classList.remove('adding__btn_z-index');
                                    document.querySelector('.adding__btn_2')?.classList.add('adding__btn_z-index');
                                }}
                                onClick={(event) => {
                                    event.preventDefault(); 
                                    store.add(description, value, false); 
                                    document.querySelectorAll('input').forEach(element => {
                                        element.value = '';
                                    });
                                }}>
                                Витрата
                            </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default observer(EditForm);
