import { useState } from 'react';
import './Menu.sass';

export let history: number;
export let statistics: number;

const selectArea = (num: number) => {
    switch (num) {
        case 1:
            history = true;
            statistics = false;
            break;
        case 2:
            history = false;
            statistics = true;
            break;
      }
}

const Menu = () => {
    return(
        <div className='menu'>
            <button onClick={() => selectArea(1)} className="menu__btn">Історія</button>
            <button onClick={() => selectArea(2)} className="menu__btn">Статистика</button>
        </div>      
    )
}

export default Menu;
